function Map({ api }) {
  const [imgKey, setImgKey] = useState("085Gpl_xNxW1Lw2eeEG28w");
  const [latlong, setLatlong] = useState({ lat: 0, long: 0 });
  const [clientID, setClientID] = useState("");
  useEffect(() => {
    fetch(`${api}/clientID`)
