import React, { useState } from "react";
import { useHistory, Link } from "react-router-dom";
import security from "./security.jpeg";

function Login({ api, setUser }) {
  const [errors, setErrors] = useState([]);
  const [userInfo, setUserInfo] = useState({
    username: "",
    password: "",
  });
  const history = useHistory();

  function handleChange(e) {
    let key = e.target.name;
    let val = e.target.value;

    setUserInfo({ ...userInfo, [key]: val });
  }

  function handleSubmit(e) {
    e.preventDefault();
    fetch(`${api}/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify(userInfo),
    })
      .then((r) => {
        return r.json().then((data) => {
          if (r.ok) {
            return data;
          } else {
            throw data;
          }
        });
      })
      .then((response) => {
        const { user, token } = response;
        localStorage.setItem("token", token);
        setUser(user);
        history.push("/home");
      })
      .catch((response) => {
        // console.log("errors console log", response.errors);
        setUserInfo({ ...userInfo, password: "" });
        setErrors(response.errors);
      });
  }

  return (
    <div
      id="welcome-holder"
      style={{
        background: `url(${deskpic})`,
        height: "100vh",
        width: "100%",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
      }}
    >
      <span id="welcome-frame">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="username"
            className="field"
            placeholder="Enter Username"
            value={userInfo.username}
            onChange={handleChange}
          ></input>
          <br />
          <input
            type="password"
            name="password"
            className="field"
            placeholder="Enter Password"
            value={userInfo.password}
            onChange={handleChange}
          ></input>
          <br />
          <button type="submit" className="field disabled">
            Login
          </button>
        </form>
        {errors.length !== 0 ? (
          <>
            {errors.map((er, i) => (
              <div key={i}>{er}</div>
            ))}
          </>
        ) : null}
        <Link id="signup-button" className="button field" to="/signup">
          First time flying? Signup for a passport!
        </Link>
      </span>
    </div>
  );
}

export default Login;
